package com.dev.test.array;/** * @version 1.0 * @Description * @Author wangbo * @Date 2021/1/6 4:30 下午 */public class MergeArray {    public static int[]  merge(int [] array1,int [] array2){        int [] result=new int[array1.length+array2.length];        return arraySort(array1,0,array2,0,result,0);    }    private static int[] arraySort(int[] a,int i, int[] b,int j,int[] c,int k) {        if (i+j==c.length){            return c;        }else if (a.length==i){            c[k++]=b[j++];        }else if (b.length==j){            c[k++]=a[i++];        }else {            if (a[i]<b[j]){                c[k++]=a[i++];            }else {                c[k++]=b[j++];            }        }        return arraySort(a,i,b,j,c,k);    }}