package com.dev.test.limit;/** * @version 1.0 * @Description * @Author wangbo * @Date 2021/1/6 5:56 下午 */public class ApiServiceImpl implements ApiService {    private  static RateLimter limiter=RateLimter.getLimiter(2);    @Override    public void query(String ip) {        if (limiter.limiter(ip)) {            System.out.println(System.currentTimeMillis()+":query something! ip:"+ip);        } else {            System.out.println(System.currentTimeMillis()+":限流了。 ip:"+ip);        }    }}